import "./Chart.css";
import ChartBar from "./ChartBar";
function Chart(props) {
    const dataPointsValue=props.dataInfo.map((value)=>{return value.value});
    const totalMax=Math.max(...dataPointsValue);
  return (
    <div className="chart">
        <ChartBar
          key={props.dataInfo.lable}
          value={props.dataInfo.value}
          maxValue={totalMax}
          lable={props.dataInfo.lable}
        />;

    </div>
  );
}
export default Chart;
