import "./Chart.css";
import ChartBar from "./ChartBar";
function Chart(props) {
    const dataPointsValue=props.dataInfo.map((dataInfo)=>{return dataInfo.value});
    const totalMax=Math.max(...dataPointsValue);
  return (
    <div className="chart">
      {props.dataInfo.map((dataInfo) => <ChartBar
          key={dataInfo.lable}
          value={dataInfo.value}
          maxValue={totalMax}
          lable={dataInfo.lable}
        />)}
    </div>
  );
}
export default Chart;
